import React from 'react'
import { useState } from 'react'
import { StyleSheet, View, TextInput, Button, TouchableOpacity } from 'react-native';
import { Text, ButtonGroup } from '@rneui/themed'
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
const Stack = createNativeStackNavigator()

export default function QuizScreen({navigation, route}) {
    let questions = route.params.questions
    let index = route.params.index
    let type = route.params.type
    let quizNum = route.params.quizNum
    let qNum = index

    const QuizType = ({ question }) => {
      if (type === 0) {
        return (
          <>
          <ButtonGroup
            buttons={questions[index].choices}
            vertical 
            selectedIndex={selectedIndex}
            onPress={(value) => {
              setSelectedIndex(value);
            }}
            buttonStyle={{ width: 200 }}
            buttonContainerStyle={{ backgroundColor: "white", height: 50 }}
            selectedButtonStyle={{ backgroundColor: "#6A89FE" }}
            containerStyle={{marginBottom: 30}}
          />
          <TouchableOpacity style={styles.button} onPress={() => {setUserAnswers([...userAnswers, selectedIndex]), setSelectedIndex(), nextScreen()}}>
            <Text style={styles.buttonText}>Next</Text>
          </TouchableOpacity>
          </>
        )
      } else if (type === 1) {
        return (
          <>
          <ButtonGroup
            buttons={questions[index].choices}
            vertical 
            selectedIndexes={selectedIndexes}
            onPress={(value) => {
              setSelectedIndexes(value);
            }}
            selectMultiple
            buttonStyle={{ width: 200 }}
            buttonContainerStyle={{ backgroundColor: "white", height: 50 }}
            selectedButtonStyle={{ backgroundColor: "#6A89FE" }}
            containerStyle={{marginBottom: 30}}
          />
          <TouchableOpacity style={styles.button} onPress={() => {setUserAnswers([...userAnswers, selectedIndexes]), setSelectedIndexes(), nextScreen()}}>
            <Text style={styles.buttonText}>Next</Text>
          </TouchableOpacity>
          </>
        )
      } else if (type === 2) {
        return (
          <>
          <View style={styles.inputContainer}>
            <Text style={styles.inputText}>= </Text>
            <TextInput style={styles.input} placeholder='' value={textInput} onChangeText={text=>setInput(text)}/>
          </View>
          <TouchableOpacity style={styles.button} onPress={() => {setUserAnswers([...userAnswers, textInput]), setSelectedIndexes(), nextScreen()}}>
            <Text style={styles.buttonText}>Next</Text>
          </TouchableOpacity>
          </>
        )
      } else if (type === 3) {
        return (
          <>
          <View style={styles.inputContainer2}>
            <TextInput style={styles.input2} placeholder='Fill In the TV-Show Title' value={textInput} onChangeText={text=>setInput(text)}/>
            <TextInput style={styles.input} placeholder='Fill In the TV-Show Title' value={firstname} onChangeText={text=>setInput(text)}/>
          </View>
          <TouchableOpacity style={styles.button} onPress={() => {setUserAnswers([...userAnswers, textInput]), setSelectedIndexes(), nextScreen()}}>
            <Text style={styles.buttonText}>Next</Text>
          </TouchableOpacity>
          </>
        )
      }
    }

    const ItemSeparatorView = () => {
        return (
            <View style={{ height: 20, width: '100%', backgroundColor: '#14151F' }}/>
        )
    }
    const renderButtons = ( {item} ) => {
        return (
            <Button color={item === selectedItem? "#353D5C" : "#6A89FE"} title={item} onPress={()=> {
                setSelected(item)
                setColor(true)
            }}></Button>
        )
    }

    const nextScreen = () => {
        if (index != 2) {
            navigation.navigate("Quiz", {questions: questions, index: ++index, type: type, quizNum: quizNum})
        } else if (index === 2) {
            userAnswers[2] = selectedIndex
            navigation.navigate("Summary", {questions: questions, a1: userAnswers[0], a2: userAnswers[1], a3: userAnswers[2]})
        }
    }
    const [selectedIndex, setSelectedIndex] = useState();
    const [selectedIndexes,setSelectedIndexes] = React.useState([]);
    const [textInput, setInput] = useState('');
    const [userAnswers, setUserAnswers] = useState([]);
    console.log({userAnswers})
    return (
        <View style={styles.container}>
            <Text style={styles.titleText}>Quiz {quizNum}</Text>
            <Text style={styles.introText}>Question #{++qNum}</Text>
            <Text style={styles.qText}>{questions[index].prompt}</Text>
            <QuizType question={questions}></QuizType>
        </View>
    )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#14151F',
    alignItems: 'center',
    justifyContent: 'center',
  },
  titleText: {
    fontSize: 60,
    color: "white",
    fontWeight: 'bold',
    padding: 15
  },
  introText: {
    fontSize: 18,
    paddingTop: 10,
    color: "#6A89FE"
  },
  button: {
    backgroundColor: "white",
    padding: 15,
    paddingHorizontal: 50,
    borderRadius: 8,
    marginBottom: 100
  },
  buttonText: {
    fontSize: 20,
    fontWeight: "bold",
    color: "#6A89FE"
  },
  qText: {
    fontSize: 22,
    padding: 30,
    color: "white",
    fontWeight: "bold"
  },
  inputContainer: {
    width: '10%',
    flexDirection: "row",
  },
  input: {
    width: '50%',
    backgroundColor: 'white',
    paddingHorizontal: 15,
    paddingVertical: 15,
    borderRadius: 20,
    marginBottom: 30,
    marginTop: 5
  },
  inputText: {
    fontSize: 25,
    paddingTop: 12,
    color: "#6A89FE",
    fontWeight: "bold",
    marginRight: 15
  },
  inputContainer2: {
    width: '30%',
  },
  input2: {
    backgroundColor: 'white',
    paddingHorizontal: 15,
    paddingVertical: 15,
    borderRadius: 20,
    marginBottom: 30,
    marginTop: 5
  },
});

