import React from 'react'
import { StyleSheet, View, FlatList } from 'react-native';
import { Text } from '@rneui/themed'
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
const Stack = createNativeStackNavigator()

export default function SummaryScreen({navigation, route}) {
    let questions = route.params.questions
    let a1 = route.params.a1
    let a2 = route.params.a2
    let a3 = route.params.a3
    let type = route.params.type
    let qNum = 0
    let points = 0

    const renderAnswers = ( {item} ) => {
        qNum++
        let correctAns = item.correct
        let correctAns2 
        let userAnswer
        if (type === 0) {
            if (qNum === 1) {
                userAnswer = a1
                if (correctAns === userAnswer) {
                    points++
                }
            } else if (qNum === 2) {
                userAnswer = a2
                if (correctAns === userAnswer) {
                    points++
                }
            } else if (qNum === 3) {
                userAnswer = a3
                if (correctAns === userAnswer) {
                    points++
                }
            }  
        }



    }
    return (
        <View style={styles.appContainer}>
            <Text style={styles.titleText}>Summary</Text>
            <FlatList data={questions} renderItem={renderAnswers} />
        </View>
    )
}

const styles = StyleSheet.create({
  appContainer: {
    flex: 1,
    backgroundColor: '#14151F',
    alignItems: 'center',
    justifyContent: 'center',
  },
  container: {
    flex: 1,
    backgroundColor: '#14151F',
    alignItems: 'center',
    justifyContent: 'center',
  },
  titleText: {
    fontSize: 50,
    color: "white",
    fontWeight: 'bold',
    padding: 15
  },
  introText: {
    fontSize: 20,
    paddingTop: 10,
    color: "#6A89FE"
  },
  qText: {
    fontSize: 18,
    paddingTop: 5,
    color: "white"
  },
  ansText: {
    fontSize: 16,
    color: "white",
    flexDirection: "row",
    justifyContent: "flex-end",
    paddingTop: 3
  },
  correctText: {
    fontSize: 20,
    paddingTop: 10,
    fontWeight: "bold",
    color: "white",
    flexDirection: "row",
    justifyContent: "flex-end",
  },
  scoreText: {
    fontSize: 50,
    color: "#6A89FE",
    fontWeight: 'bold',
    padding: 30
  }
});
