import React from 'react'
import { StyleSheet, View, TouchableOpacity } from 'react-native';
import { Text } from '@rneui/themed'
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
const Stack = createNativeStackNavigator()

export default function HomeScreen({navigation}) {
    return (
        <View style={styles.container}>
            <Text style={styles.titleText}>Home</Text>
            <Text style={styles.introText}>Select A Quiz</Text>
            <View style={styles.buttonContainer}>
                <TouchableOpacity style={styles.button} onPress={() => navigation.navigate("Quiz")}>
                    <Text style={styles.buttonText}>Quiz 1</Text>
                    <Text style={styles.buttonText2}>Multiple Choice</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.button} onPress={() => navigation.navigate("Quiz")}>
                    <Text style={styles.buttonText}>Quiz 2</Text>
                    <Text style={styles.buttonText2}> Multi - Answer</Text>
                </TouchableOpacity>
            </View>
            <View style={styles.buttonContainer}>
                <TouchableOpacity style={styles.button} onPress={() => navigation.navigate("Quiz")}>
                    <Text style={styles.buttonText}>Quiz 3</Text>
                    <Text style={styles.buttonText2}>True or False</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.button} onPress={() => navigation.navigate("Quiz")}>
                    <Text style={styles.buttonText}>Quiz 4</Text>
                    <Text style={styles.buttonText2}>Math Problems</Text>
                </TouchableOpacity>
            </View>
        </View>
    )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#14151F',
    alignItems: 'center',
    justifyContent: 'center',
  },
  titleText: {
    fontSize: 50,
    color: "white",
    fontWeight: 'bold',
    padding: 15
  },
  introText: {
    fontSize: 20,
    paddingTop: 10,
    color: "#6A89FE",
    marginBottom: 50
  },
  button: {
    backgroundColor: "#413F8C",
    padding: 30,
    paddingHorizontal: 5,
    borderRadius: 8,
    alignItems: "center",
    marginLeft: 30
  },
  buttonText: {
    fontSize: 20,
    fontWeight: "bold",
    color: "white"
  },
  buttonText2: {
    fontSize: 14,
    color: "white",
    margin: 10
  },
  buttonContainer: {
    flexDirection: "row",
  }
});
